    <script>
    
    $(document).ready(function(){
       $("#mark_all").change(function () {
		$(".my_selected").prop('checked', $(this).prop("checked"));
	});

	$('.my_selected').change(function(){ 
		if(false == $(this).prop("checked")){ //if this item is unchecked
			$("#mark_all").prop('checked', false); //change "select all" checked status to false
			
		}
		if ($('#mark_all:checked').length == $('#mark_all').length ){
			$("#mark_all").prop('checked', true);
		}
	});
       
    });
    
    
    </script>

    @stack('js')
    @yield('js')
